<script type="text/javascript">
    window.onload = function () {
        videojs("qtcPlayer", {
            controls: true,
            techOrder: ["youtube", "html5"],
            youtube: { "customVars": { "showinfo": 0, "iv_load_policy" : 3 }},
            sources: [
                { "type": "video/youtube", "src": $('#qtcPlayer').data("url") }
            ]
        }, function() {
            var mPlayer = this
            $("a[data-video]").on("click", function (e) {
                e.preventDefault();
                mPlayer.poster( $(this).data("poster"))
                mPlayer.src({ type: "video/youtube", src: $(this).attr("href") });
                mPlayer.play();
            });
        });
    }

    $('.responsive').slick({
        dots: true,
        infinite: false,
        speed: 300,
        slidesToShow: 4,
        slidesToScroll: 4,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
            // You can unslick at a given breakpoint now by adding:
            // settings: "unslick"
            // instead of a settings object
        ]
    });
</script>
